mapper.select:
select ur.id,ur.user_id as userId,ur.role_id as roleId, r.name as roleName from x_user_role ur
left join x_role r on ur.role_id = r.id
where ur.user_id = #{userId}

select ud.id,ud.user_id as userId,d.dept_name as deptName from x_user_dept ud
left join x_dept d on ud.dept_id = d.id
where ud.user_id = #{userId}

select distinct m.id,m.name,m.path,m.parent_id,m.icon,m.order_num from x_menu m left join x_role_menu rm on m.id = rm.menu_id where rm.role_id in
#{roleId}
ORDER BY m.order_num ASC
